/* Set wmode=transparent for iframes to show it under the menus, lightboxes etc. */
jQuery(function ($) {
    "use strict";
    $("iframe[src]").each(function () {
        var iframe = $(this);
        var src = iframe.attr("src");
        if (src.lastIndexOf("?") !== -1) {
            return;
        } else {
            src += "?wmode=transparent";
        }
        iframe.attr("src", src);
    });
});

